import React, { Component } from 'react';
import './style.css';

class  ImageCarouselChild extends Component {

  static slug = 'inftnc_image_carousel_child';

  render() {
    const { image, alt } = this.props;
    const imageValue = image ? image : 'data:image/svg+xml;base64,';

    return (
      <div className="inftnc_carousel_child">
        <img className="logo_carousel_img" src={imageValue} alt={alt} />
      </div>
    );
  }
}

export default ImageCarouselChild;
